import React from "react";
import { motion } from "framer-motion";

const Offsets = () => {
  return (
    <div className="container mt-5">

      {/* Hero Section */}
      <motion.div
        className="text-center p-5 bg-light rounded shadow"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        <h1 className="display-5 text-success fw-bold">
          Rising Carbon Footprint = Rising Temperatures
        </h1>
        <p className="lead text-secondary mt-3">
          Every fraction of a degree matters. The choices we make today impact the planet we leave behind.
        </p>
      </motion.div>

      {/* Explanation Card */}
      <motion.div
        className="card mt-5 p-4 fixed-card mx-auto shadow"
        style={{ maxWidth: "800px" }}
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 1.2, delay: 0.3 }}
      >
        <h4 className="card-title mb-3 text-center text-dark">Why Should You Care?</h4>
        <p className="card-text text-muted">
          Our everyday habits—commuting, eating, shopping, even scrolling—leave behind a carbon trail.
          <br /><br />
          The Earth’s temperature is rising. Glaciers are melting, extreme weather is becoming more frequent,
          and ecosystems are collapsing. If we don’t act now, our future generations will pay the price.
          <br /><br />
          🌱 Small efforts like offsetting your carbon emissions, switching to clean energy, and mindful
          consumption can help slow this trajectory. Be part of the change.
        </p>
      </motion.div>

      {/* Animated Image Cards */}
      <div className="row text-center mt-5">
        {[
          { src: "images/images/Student_data.png", caption: "Student Life Impact" },
          { src: "images/images/one.png", caption: "Every Action Counts" },
          { src: "images/images/commute.png", caption: "Daily Commute Matters" },
          { src: "images/images/CEO.png", caption: "Corporate Responsibility" },
          { src: "images/images/Flyer.png", caption: "Flying vs Earth Health" },
        ].map((item, index) => (
          <motion.div
            className="col-md-4 mb-4"
            key={index}
            whileHover={{ scale: 1.05 }}
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: index * 0.2 }}
          >
            <div className="card fixed-card shadow-sm p-3">
              <img
                src={item.src}
                alt={item.caption}
                className="img-fluid mb-2"
                style={{ maxHeight: "200px", objectFit: "contain" }}
              />
              <h6 className="text-dark">{item.caption}</h6>
            </div>
          </motion.div>
        ))}
      </div>

      {/* Call to Action */}
      <motion.div
        className="text-center mt-5 p-4 bg-success text-white rounded"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1.2 }}
      >
        <h3>🌍 Join the Mission to Reduce Carbon Emissions</h3>
        <p className="mb-3">
          Whether you're a student, traveler, or CEO — your carbon choices matter.
        </p>
        <button className="btn btn-light px-4 py-2 fw-bold">
          Calculate Your Carbon Footprint
        </button>
      </motion.div>
    </div>
  );
};

export default Offsets;
