import React, { useEffect } from "react";
import { Link } from 'react-router-dom';
import "../Component/About.css";

export default function About() {
  useEffect(() => {
    const handleScroll = () => {
      const reveals = document.querySelectorAll(".reveal");
      reveals.forEach((element) => {
        const windowHeight = window.innerHeight;
        const elementTop = element.getBoundingClientRect().top;
        const elementVisible = 150;

        if (elementTop < windowHeight - elementVisible) {
          element.classList.add("active");
        } else {
          element.classList.remove("active");
        }
      });
    };

    window.addEventListener("scroll", handleScroll);
    // Initial check for elements in view
    handleScroll();

    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="about-container">
      <main>
        <section id="front" className="hero-section reveal">
          <h1>Welcome To CarbonCalc</h1>
          <img src="images/images/Aboutlanding0.jpeg" alt="Carbon footprint" className="hero-image" />
          <p className="hero-text">
            We strive to make carbon emission reduction simple and accessible for everyone. Our platform empowers you to take informed steps toward minimizing your environmental impact. Together, we can build a sustainable future by adopting cleaner practices and offsetting emissions effectively.
          </p>
        </section>

        <section id="first" className="reveal">
          <img src="images/images/Aboutlanding.jpeg" alt="Carbon management" className="section-image" />
          <div className="section-content">
            <h1>Comprehensive Solutions for Carbon Footprint Management</h1>
            <p>
              Our carbon website helps individuals measure, reduce, and offset their carbon footprint. We provide accurate calculations, personalized reduction plans, and carbon offsetting programs to support sustainability efforts. With real-time tracking, expert guidance, and educational resources, we empower users to make informed choices and work towards carbon neutrality.
            </p>
          </div>
        </section>

        <section id="fourth" className="features-section reveal">
          <h1>Empowering Sustainability Through Carbon Footprint Management</h1>
          <div className="feature-cards">
            {[
              { icon: "data-analysis", title: "Tailored Carbon Solutions" },
              { icon: "ui-ux-designer", title: "Seamless Carbon Offsetting" },
              { icon: "web-development", title: "Real-Time Tracking & Insights" },
              { icon: "qa-testing", title: "Sustainability Education" },
              { icon: "team", title: "Continuous Support" }
            ].map((feature, index) => (
              <div key={index} className="feature-card reveal">
                <img
                  src={`https://cdn.iconscout.com/icon/premium/png-64-thumb/${feature.icon}-27-681042.png`}
                  alt={feature.title}
                />
                <p>{feature.title}</p>
              </div>
            ))}
          </div>
        </section>

        <section id="second" className="info-section">
          {[
            {
              title: "WE HAVE",
              subtitle: "Advanced Technology and Expertise to Drive Sustainability",
              text: "We have a dedicated team of experts and advanced technology to offer tailored solutions for carbon reduction. Our platform integrates reliable data and insights to help users make sustainable choices in real-time.",
              image: "business-partnership-2975816-2476892"
            },
            {
              title: "OUR GOAL",
              subtitle: "Empowering Action Towards a Greener Future",
              text: "Our goals are to lead the way in carbon reduction by promoting sustainable practices and empowering people to take action. We aim to create a greener future by providing actionable solutions that reduce environmental impact across industries.",
              image: "business-goal-4352585-3618767"
            },
            {
              title: "OUR STRENGTHS",
              subtitle: "Innovative, Data-Driven Solutions",
              text: "Our strength lies in our innovative approach, combining cutting-edge technology with environmental expertise. We deliver personalized, data-driven solutions to help users offset their emissions effectively and track their progress.",
              image: "teamwork-3560853-2989144"
            }
          ].map((section, index) => (
            <div key={index} className="info-container reveal">
              <div className="info-content">
                <h1>{section.title}</h1>
                <h2>{section.subtitle}</h2>
                <p>{section.text}</p>
              </div>
              <img
                src={`https://cdni.iconscout.com/illustration/premium/thumb/${section.image}.png`}
                alt={section.title}
              />
            </div>
          ))}
        </section>

        <section id="fifth" className="contact-section reveal">
          <h1>CARBONCALC</h1>
          <div className="contact-info">
            {[
              { icon: "address-blue-circle-location-map-marker-navigation-icon-45868", title: "Address", text: "Srinivaspura, India" },
              { icon: "phone-2666572-2212584", title: "Phone", text: "+1 248 672 1972" },
              { icon: "gmail-2489176-2082900", title: "E-mail", text: "Pratikshawali89@gmail.com" }
            ].map((contact, index) => (
              <a key={index} href="#" className="contact-card">
                <img src={`https://cdn.iconscout.com/icon/premium/png-64-thumb/${contact.icon}.png`} alt={contact.title} />
                <span>
                  <h3>{contact.title}</h3>
                  <p>{contact.text}</p>
                </span>
              </a>
            ))}
          </div>
        </section>
      </main>

      <footer className="footer">
        <div className="footer-links">
          <ul>
            <li><Link to="/home">Calculator</Link></li>
            <li><Link to="/projects">Projects</Link></li>
            <li><Link to="/learninfo">Blogs</Link></li>
          </ul>
          <ul>
            <li><Link to="/about">About Us</Link></li>
            <li><Link to="/FAQS">Frequently Asked Questions</Link></li>
            <li><Link to="/contactus">Contact Us</Link></li>
          </ul>
          <div className="social-links">
            <h2>Contact Us</h2>
            <div className="social-icons">
              {['instagram', 'facebook', 'whatsapp', 'telegram', 'linkedin'].map((platform, index) => (
                <a key={index} href="#">
                  <img src={`https://cdn.iconscout.com/icon/free/png-64/${platform}-188-498425.png`} alt={platform} />
                </a>
              ))}
            </div>
            <a href="tel:123456789" className="phone-number">
              Telephone No: +91 232345553
            </a>
          </div>
        </div>
      </footer>
      <div className="copyright">
        &copy; Copyright <b>CarbonCalc</b>. All Rights Reserved
      </div>
    </div>
  );
}
