import React, { useState, useEffect } from "react"; 
import { Link, useNavigate } from "react-router-dom";
import axios from "axios"; 
import "./Navbar.css";

const Navbar = () => {
  const [user, setUser] = useState(null); // State to store user data
  const [isScrolled, setIsScrolled] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Add scroll event listener
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener("scroll", handleScroll);

    // Fetch user profile on component mount
    const fetchUserProfile = async () => {
      const token = localStorage.getItem("jwtToken"); // Get token from localStorage
      console.log("Token:", token);
      if (token) {
        try {
          const response = await axios.get("http://localhost:8080/profile", {
            headers: { Authorization: `Bearer ${token}` }, // Pass token in the Authorization header
          });
          console.log("User Profile Response:", response.data);
          setUser(response.data); // Set user data in state
        } catch (error) {
          console.error("Error fetching user profile:", error);
          localStorage.removeItem("jwtToken"); // Clear invalid token
          setUser(null);
        }
      }
    };

    fetchUserProfile();
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("jwtToken"); // Clear the token
    setUser(null); // Reset user data in state
    navigate("/login"); // Redirect to login page
  };

  return (
    <nav className={`navbar ${isScrolled ? "scrolled" : ""}`}>
      <div className="navbar-logo">
        <span className="logo-icon">ðŸŒ±</span>
        <span className="logo-text">CarbonCalc</span>
      </div>
      <ul className="navbar-links">
        <li><Link to="/home">Calculator</Link></li>
        <li><Link to="/why-carbon">Why Carbon?</Link></li>
        <li><Link to="/projects">Projects</Link></li>
        <li><Link to="/about">About Us</Link></li>
      </ul>
      <div className="navbar-actions">
        {user ? (
          <div className="user-profile">
            <img
              src={user.avatar || "https://via.placeholder.com/40"} // Display user avatar
              alt="Profile"
              className="avatar"
            />
            <span className="username">{user.name}</span> {/* Display user name */}
            <Link to="/profile" className="button outline">
              Profile
            </Link>
            <button className="logout-button" onClick={handleLogout}>
              Logout
            </button>
          </div>
        ) : (
          <>
            <Link className="button outline" to="/login">
              Login
            </Link>
            <Link className="button primary marg" to="/signup">
              Sign Up
            </Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
