import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Navbar.css";

const Navbar = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showProfileCard, setShowProfileCard] = useState(false);
  const [user, setUser] = useState({ name: '', email: '' });

  // Check if the user is logged in on component mount
  useEffect(() => {
    const token = localStorage.getItem("jwtToken");
    setIsLoggedIn(!!token); // Set true if token exists, false otherwise
    setUserName(localStorage.getItem("userName"));
    setUserEmail(localStorage.getItem("userEmail"));
    
  }, []); // This will run once when the component mounts
  
  
  const handleScroll = () => {
    setIsScrolled(window.scrollY > 50);
  };


  useEffect(() => {
        const fetchUserDetails = async () => {
            try {
                const token = localStorage.getItem('token'); // Get JWT token from local storage
                const response = await fetch('/auth/user-details', {
                    method: 'GET',
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });

                const data = await response.json();
                if (data.success) {
                    setUser({ name: data.name, email: data.email }); // Update user details
                } else {
                    console.error('Failed to fetch user details:', data.message);
                }
            } catch (error) {
                console.error('Error fetching user details:', error);
            }
        };

        fetchUserDetails();
    }, []);









  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("jwtToken"); // Remove token
    localStorage.removeItem("userName"); // Remove userName
    localStorage.removeItem("userEmail"); // Remove userEmail
    setIsLoggedIn(false); // Update state
    setShowProfileCard(false); // Close the card
    setUserName(null); // Reset userName
    setUserEmail(null); // Reset userEmail
  };

  const toggleProfileCard = () => {
    setShowProfileCard((prev) => !prev);
  };

  return (
    <nav className={`navbar ${isScrolled ? "scrolled" : ""}`}>
      {/* Logo Section */}
      <div className="navbar-logo">
        <span className="logo-icon">ðŸŒ±</span>
        <span className="logo-text">CarbonCalc</span>
      </div>

      {/* Navigation Links */}
      <ul className="navbar-links">
        <li>
          <Link to="/home">Calculator</Link>
        </li>
        <li>
          <Link to="/why-carbon">Why Carbon?</Link>
        </li>
        <li>
          <Link to="/projects">Projects</Link>
        </li>
        <li>
          <Link to="/about">About Us</Link>
        </li>
      </ul>

      {/* Action Buttons */}
      <div className="navbar-actions">
        {isLoggedIn ? (
          <>
            <button className="button outline" onClick={toggleProfileCard}>
              Profile
            </button>
            <button className="button primary marg" onClick={handleLogout}>
              Logout
            </button>
          </>
        ) : (
          <>
            <Link className="button outline" to="/login">
              Login
            </Link>
            <Link className="button primary marg" to="/signup">
              Sign Up
            </Link>
          </>
        )}
      </div>

      {/* Profile Card */}
      {showProfileCard && (
        <div className="profile-card">
          <h3>User Profile</h3>
          <p><strong>Name:</strong> {userName || "Guest"}</p>
          <p><strong>Email:</strong> {userEmail || "Not Provided"}</p>
          <button className="button primary marg cardlog" onClick={handleLogout}>
            Logout
          </button>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
