import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Leaf, Calculator, Globe, InfoIcon, LogIn, UserPlus } from "lucide-react";

const Navbar = () => {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <nav 
      className={`
        fixed top-0 left-0 w-full z-50 transition-all duration-300 
        ${isScrolled 
          ? 'bg-white/90 shadow-md backdrop-blur-md' 
          : 'bg-transparent'
        }
      `}
    >
      <div className="max-w-6xl mx-auto px-4 py-3 flex justify-between items-center">
        {/* Logo Section */}
        <div className="flex items-center space-x-3">
          <Leaf 
            className={`
              text-green-600 transition-transform duration-300
              ${isScrolled ? 'rotate-6 scale-110' : 'rotate-0'}
            `} 
            size={32} 
          />
          <span className={`
            font-bold text-xl transition-colors duration-300
            ${isScrolled ? 'text-gray-800' : 'text-white'}
          `}>
            CarbonCalc
          </span>
        </div>

        {/* Navigation Links */}
        <ul className="flex space-x-6">
          {[
            { to: "/home", icon: Calculator, text: "Calculator" },
            { to: "/why-carbon", icon: Globe, text: "Why Carbon?" },
            { to: "/projects", icon: Leaf, text: "Projects" },
            { to: "/about", icon: InfoIcon, text: "About Us" }
          ].map(({ to, icon: Icon, text }) => (
            <li key={to}>
              <Link 
                to={to} 
                className={`
                  flex items-center space-x-2 
                  group transition-all duration-300
                  ${isScrolled 
                    ? 'text-gray-700 hover:text-green-600' 
                    : 'text-white hover:text-green-200'
                  }
                  hover:scale-105
                `}
              >
                <Icon 
                  size={18} 
                  className={`
                    transition-transform duration-300 
                    group-hover:rotate-12
                  `} 
                />
                <span className="font-medium">{text}</span>
              </Link>
            </li>
          ))}
        </ul>

        {/* Action Buttons */}
        <div className="flex space-x-4">
          {[
            { to: "/login", icon: LogIn, text: "Login", variant: "outline" },
            { to: "/signup", icon: UserPlus, text: "Sign Up", variant: "primary" }
          ].map(({ to, icon: Icon, text, variant }) => (
            <Link
              key={to}
              to={to}
              className={`
                flex items-center space-x-2 px-4 py-2 rounded-full 
                font-semibold transition-all duration-300 
                ${variant === 'primary' 
                  ? 'bg-green-600 text-white hover:bg-green-700' 
                  : 'border border-green-600 text-green-600 hover:bg-green-50'
                }
                hover:shadow-md transform hover:-translate-y-1
              `}
            >
              <Icon size={18} />
              <span>{text}</span>
            </Link>
          ))}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
