import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios"; // For API calls
import "../css/Navbar.css"; // Adjust the path if needed

const Navbar = () => {
  const [user, setUser] = useState(null); // State to store user data
  const [isScrolled, setIsScrolled] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener("scroll", handleScroll);

    const fetchUserProfile = async () => {
      const token = localStorage.getItem("jwtToken"); // Get token from localStorage
      if (token) {
        try {
          const response = await axios.get("http://localhost:8080/auth/profile", {
            headers: { Authorization: `Bearer ${token}` },
          });
          setUser(response.data); // Set user data in state
        } catch (error) {
          console.error("Error fetching user profile:", error);
          localStorage.removeItem("jwtToken");
          setUser(null);
        }
      }
    };

    fetchUserProfile();
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("jwtToken");
    setUser(null);
    navigate("/login");
  };

  return (
    <nav className={`navbar ${isScrolled ? "scrolled" : ""}`}>
      <div className="navbar-logo">
        <span className="logo-icon">ðŸŒ±</span>
        <span className="logo-text">CarbonCalc</span>
      </div>
      <ul className="navbar-links">
        <li><Link to="/home">Calculator</Link></li>
        <li><Link to="/why-carbon">Why Carbon?</Link></li>
        <li><Link to="/projects">Projects</Link></li>
        <li><Link to="/about">About Us</Link></li>
      </ul>
      <div className="navbar-actions">
        {user ? (
          <div className="user-profile">
            <img
              src={user.avatar || "https://via.placeholder.com/40"}
              alt="Profile"
              className="avatar"
            />
            <span className="username">{user.name}</span>
            <button className="logout-button" onClick={handleLogout}>
              Logout
            </button>
          </div>
        ) : (
          <>
            <Link className="button outline" to="/login">
              Login
            </Link>
            <Link className="button primary marg" to="/signup">
              Sign Up
            </Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
