import React from "react";
import { motion } from "framer-motion";

const data = [
  {
    title: "We're Heating Up 🌡️",
    text: "Global temperatures have already risen over 1°C since pre-industrial times. Every 0.1°C increase accelerates disasters—melting glaciers, rising seas, and scorching heatwaves.",
    image: "images/images/earth_heat.png",
  },
  {
    title: "Invisible Emissions, Real Damage 🌫️",
    text: "From our morning commute to weekend flights, carbon is silently building up in the atmosphere. And it's staying there—some of it for hundreds of years.",
    image: "images/images/commute.png",
  },
  {
    title: "You Have the Power 🫵",
    text: "Switch to clean energy, travel smart, consume consciously. The planet doesn’t need perfection, it needs participation. Starting with you.",
    image: "images/images/Student_data.png",
  },
];

const Offsets = () => {
  return (
    <div className="offsets-container" style={{ backgroundColor: "#f0f9f5", padding: "50px 20px" }}>
      <motion.h1
        className="text-center fw-bold display-5 mb-5 text-success"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        Carbon Is Rising. So Is the Risk.
      </motion.h1>

      {data.map((section, index) => (
        <motion.div
          key={index}
          className="d-flex flex-column flex-md-row align-items-center justify-content-between gap-5 mb-5"
          initial={{ opacity: 0, y: 40 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: index * 0.2 }}
          viewport={{ once: true }}
        >
          {index % 2 === 0 ? (
            <>
              <img
                src={section.image}
                alt={section.title}
                className="img-fluid"
                style={{ maxWidth: "45%", borderRadius: "16px", boxShadow: "0 8px 20px rgba(0,0,0,0.1)" }}
              />
              <div style={{ maxWidth: "50%" }}>
                <h3 className="text-dark fw-semibold">{section.title}</h3>
                <p className="text-muted fs-5">{section.text}</p>
              </div>
            </>
          ) : (
            <>
              <div style={{ maxWidth: "50%" }}>
                <h3 className="text-dark fw-semibold">{section.title}</h3>
                <p className="text-muted fs-5">{section.text}</p>
              </div>
              <img
                src={section.image}
                alt={section.title}
                className="img-fluid"
                style={{ maxWidth: "45%", borderRadius: "16px", boxShadow: "0 8px 20px rgba(0,0,0,0.1)" }}
              />
            </>
          )}
        </motion.div>
      ))}

      <motion.div
        className="text-center mt-5"
        initial={{ opacity: 0, scale: 0.9 }}
        whileInView={{ opacity: 1, scale: 1 }}
        transition={{ duration: 1 }}
      >
        <h2 className="text-success fw-bold mb-3">Ready to Make an Impact?</h2>
        <p className="text-secondary mb-4 fs-5">
          Offset your emissions. Choose sustainability. Be the reason the Earth heals.
        </p>
        <button className="btn btn-success btn-lg px-5 py-2 shadow">
          🌍 Calculate My Carbon Footprint
        </button>
      </motion.div>
    </div>
  );
};

export default Offsets;
