import React, { useEffect } from 'react';
import './Landing.css'; // Import CSS styles

const Landing = () => {
  useEffect(() => {
    const smokeElements = document.querySelectorAll('.smoke');

    smokeElements.forEach((smoke, index) => {
      smoke.style.left = `${Math.random() * 100}%`;
      smoke.style.top = `${Math.random() * 100}%`;
      smoke.style.animationDelay = `${index * 3}s`;
    });
  }, []);

  return (
    <div className="smoke-container" id="smokeContainer">
      {Array.from({ length: 11 }).map((_, index) => (
        <div
          key={index}
          className={index % 2 === 0 ? 'smoke' : 'smoke1'}
        ></div>
      ))}
      <div className="content">
        
          <h1 className="heading">Carbon Footprint Calculator</h1>
          <p className="subtitle">Understand and Reduce Your Environmental Impact</p>
          <a href="#" class="cta-button">Calculate Now</a>
        >
      </div>
    </div>
  );
};

export default Landing;
