import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Navbar.css";

const Navbar = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Check if userId exists in localStorage when the component mounts
    const userId = localStorage.getItem("userId");
    if (userId) {
      setIsLoggedIn(true);
    }

    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleLogin = () => {
    // Simulate a user login by storing a userId in localStorage
    localStorage.setItem("userId", "user123");
    setIsLoggedIn(true);
  };

  return (
    <nav className={`navbar ${isScrolled ? "scrolled" : ""}`}>
      {/* Logo Section */}
      <div className="navbar-logo">
        <span className="logo-icon">ðŸŒ±</span>
        <span className="logo-text">CarbonCalc</span>
      </div>

      {/* Navigation Links */}
      <ul className="navbar-links">
        <li>
          <Link to="/home">Calculator</Link>
        </li>
        <li>
          <Link to="/why-carbon">Why Carbon?</Link>
        </li>
        <li>
          <Link to="/projects">Projects</Link>
        </li>
        <li>
          <Link to="/about">About Us</Link>
        </li>
      </ul>

      {/* Action Buttons */}
      <div className="navbar-actions">
        {isLoggedIn ? (
          // Profile picture when logged in
          <img
            src="https://via.placeholder.com/40"
            alt="Profile"
            className="profile-pic"
            style={{
              width: "40px",
              height: "40px",
              borderRadius: "50%",
              border: "2px solid green",
            }}
          />
        ) : (
          // Login and Sign-Up buttons when not logged in
          <>
            <button className="button outline" onClick={handleLogin}>
              Login
            </button>
            <Link className="button primary marg" to="/signup">
              Sign Up
            </Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
