import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { toast, ToastContainer, Bounce } from "react-toastify";
import axios from "axios";
import "react-toastify/dist/ReactToastify.css";
import "./Logincss.css";

function Login() {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleEmailChange = (e) => setEmail(e.target.value);
  const handlePasswordChange = (e) => setPassword(e.target.value);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/auth/login', { email, password });
      if (response.data.success) {
        localStorage.setItem("jwtToken", response.data.jwtToken);
        navigate("/home");
      } else {
        toast.error(response.data.message, { position: "top-center", autoClose: 5000 });
      }
    } catch (error) {
      toast.error("Login failed, please try again.", { position: "top-center", autoClose: 5000 });
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h1>Login</h1>
        <form onSubmit={handleSubmit}>
          <label>Email Address</label>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={handleEmailChange}
            required
          />
          <label>Password</label>
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={handlePasswordChange}
            required
          />
          <button type="button" className="forgot-password" onClick={() => alert("Coming soon!")}>
            Forgot password?
          </button>
          <button type="submit" className="login-button">Login</button>
        </form>
      </div>
      <ToastContainer />
    </div>
  );
}

export default Login;
