import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Navbar.css";

const Navbar = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [user, setUser] = useState(null); // Track the logged-in user

  useEffect(() => {
    // Check if user data exists in local storage or session
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (storedUser) {
      setUser(storedUser);
    }

    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleLogout = () => {
    // Clear user data on logout
    localStorage.removeItem("user");
    setUser(null);
  };

  return (
    <nav className={`navbar ${isScrolled ? "scrolled" : ""}`}>
      {/* Logo Section */}
      <div className="navbar-logo">
        <span className="logo-icon">ðŸŒ±</span>
        <span className="logo-text">CarbonCalc</span>
      </div>

      {/* Navigation Links */}
      <ul className="navbar-links">
        <li>
          <Link to="/home">Calculator</Link>
        </li>
        <li>
          <Link to="/why-carbon">Why Carbon?</Link>
        </li>
        <li>
          <Link to="/projects">Projects</Link>
        </li>
        <li>
          <Link to="/about">About Us</Link>
        </li>
      </ul>

      {/* Action Buttons or Profile */}
      <div className="navbar-actions">
        {user ? (
          <div className="user-profile">
            <img
              src={user.avatar || "https://via.placeholder.com/40"} // Default avatar
              alt="Profile"
              className="avatar"
            />
            <span className="username">{user.name}</span>
            <button className="logout-button" onClick={handleLogout}>
              Logout
            </button>
          </div>
        ) : (
          <>
            <Link className="button outline" to="/login">
              Login
            </Link>
            <Link className="button primary marg" to="/signup">
              Sign Up
            </Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
