import React from "react";
import "../Component/LearnInfo.css";

const LearnInfo = () => {
  return (
    <main className="container-fluid">
      {/* Hero Section */}
      <div className="bg-image-section">
        <div className="hero-content">
          <h1 className="hero-title">
            Explore actionable insights to reduce carbon emissions
          </h1>
          <p className="hero-description">
            Unlock valuable knowledge on reducing carbon emissions and embracing sustainable practices. This section offers expert insights, practical steps, and the latest innovations to help you minimize your environmental impact.
          </p>
        </div>
      </div>

      {/* Cards Section */}
      <div className="cards-container">
        {[
          {
            title: "Switch to Renewable Energy",
            description:
              "Solar and wind power can significantly reduce your carbon footprint.",
            link: "https://www.un.org/en/climatechange/raising-ambition/renewable-energy",
            imgSrc: "images/images/renewbale.jpeg",
          },
          {
            title: "Embrace Sustainable Agriculture",
            description:
              "Sustainable farming reduces emissions and promotes soil health.",
            link: "https://sentientmedia.org/sustainable-agriculture/",
            imgSrc: "images/images/renewable2.jpeg",
          },
          {
            title: "Adopt Sustainable Transportation",
            description:
              "Electric vehicles, carpooling, and public transport reduce travel emissions.",
            link: "https://www.energy.gov/eere/sustainable-transportation-and-fuels",
            imgSrc: "images/images/renewable3.jpeg",
          },
          {
            title: "Reduce, Reuse, Recycle",
            description:
              "Minimizing waste and recycling materials reduces emissions from landfills.",
            link: "https://solarschools.net/knowledge-bank/sustainability/reduce-reuse-recycle",
            imgSrc: "images/images/renewable4.jpeg",
          },
        ].map((card, index) => (
          <div key={index} className="card">
            <img src={card.imgSrc} alt={card.title} className="card-image" />
            <div className="card-content">
              <h3 className="card-title">{card.title}</h3>
              <p className="card-description">{card.description}</p>
              <a href={card.link} className="card-link">
                Continue reading
              </a>
            </div>
          </div>
        ))}
      </div>

      {/* Carbon Bulletin */}
      <div className="carbon-bulletin">
        <h3 className="section-title">THE CARBON BULLETIN</h3>
        <div className="news-container">
          {[
            {
              title: "Global Emission Trends",
              description:
                "Tracking the latest data and reports on carbon emissions worldwide.",
              imgSrc: "images/images/news1.jpeg",
              link: "",
            },
            {
              title: "Policy Shifts for Sustainability",
              description:
                "Updates on government policies and their impact on carbon emissions.",
              imgSrc: "images/images/news2.jpeg",
              link: "https://www.ey.com/en_in/insights/government-public-sector/six-ways-that-governments-can-drive-the-green-transition",
            },
            {
              title: "Industry Transformation Stories",
              description:
                "How major industries are embracing green practices and lowering emissions.",
              imgSrc: "images/images/news3.jpeg",
              link: "https://media.inti.asia/read/green-industrial-transformation-addressing-climate-change-and-global-warming",
            },
          ].map((news, index) => (
            <div key={index} className="news-card">
              <a href={news.link}>
                <img src={news.imgSrc} alt={news.title} className="news-image" />
              </a>
              <h2 className="news-title">{news.title}</h2>
              <p className="news-description">{news.description}</p>
            </div>
          ))}
        </div>
      </div>
    </main>
  );
};

export default LearnInfo;
