import React, { useEffect } from "react";
import AOS from "aos";
import "aos/dist/aos.css";

const Offsets = () => {
  useEffect(() => {
    AOS.init({ duration: 1000 });
  }, []);

  const cards = [
    {
      title: "We All Emit Carbon",
      description:
        "Whether it's driving, flying, or even streaming—our daily choices release carbon into the atmosphere. Small steps matter, but we must do more.",
      img: "/images/one.png",
    },
    {
      title: "The Student",
      description:
        "The average student emits 5.9 metric tons of CO₂ annually. Even remote learning has a digital footprint.",
      img: "/images/Student_data.png",
    },
    {
      title: "The Commuter",
      description:
        "Carpooling, walking, or cycling can drastically reduce carbon emissions from daily transport.",
      img: "/images/commute.png",
    },
    {
      title: "The CEO",
      description:
        "Corporate leaders can drive large-scale carbon reduction through green policies and conscious investments.",
      img: "/images/CEO.png",
    },
    {
      title: "The Frequent Flyer",
      description:
        "Air travel has one of the highest emissions. Opting for fewer flights or carbon offsets makes a difference.",
      img: "/images/Flyer.png",
    },
  ];

  return (
    <div className="px-4 py-5" style={{ backgroundColor: "#f4f4f4" }}>
      <div className="text-center mb-5" data-aos="fade-up">
        <h1 className="display-5 fw-bold text-success">
          Rising Carbon Footprint = Rising Temperatures
        </h1>
        <p className="lead mt-3">
          Since the Industrial Revolution, our emissions have spiked
          dramatically. Every choice we make affects the planet—it's time to
          take responsibility.
        </p>
      </div>

      <div className="row g-4 justify-content-center">
        {cards.map((card, index) => (
          <div className="col-md-4" key={index} data-aos="zoom-in-up">
            <div
              className="card h-100 shadow-lg border-0"
              style={{ borderRadius: "1rem" }}
            >
              <img
                src={card.img}
                alt={card.title}
                className="card-img-top"
                style={{
                  height: "220px",
                  objectFit: "cover",
                  borderTopLeftRadius: "1rem",
                  borderTopRightRadius: "1rem",
                }}
              />
              <div className="card-body d-flex flex-column">
                <h5 className="card-title text-success">{card.title}</h5>
                <p className="card-text flex-grow-1">{card.description}</p>
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="text-center mt-5" data-aos="fade-up">
        <h3 className="mb-3">Ready to Reduce Your Footprint?</h3>
        <p className="lead">
          Join the movement towards a greener, sustainable planet.
        </p>
        <div>
          <a href="/calculator" className="btn btn-success mx-2">
            Calculate Your Carbon Footprint
          </a>
          <a href="/signup" className="btn btn-outline-success mx-2">
            Join the Movement
          </a>
        </div>
      </div>
    </div>
  );
};

export default Offsets;
